// src/half-duplex-request.ts
function install() {
  class Request extends globalThis.Request {
    constructor(input, init) {
      if (init && init.body && typeof init.body[Symbol.asyncIterator] === "function" && !init.duplex) {
        init = { ...init, duplex: "half" };
      }
      super(input, init);
    }
  }
  Object.defineProperty(globalThis, "Request", {
    value: Request,
    configurable: true
  });
}

export {
  install
};
